import React, { Component } from 'react';
import Header from '../Header';
import styles from './ProductsPage.module.css';
import ProductListView from './ProductListView';
import * as actions from '../../actions/productActions';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import FilterBar from '../FilterBar';

class ProductsPage extends Component {

    constructor(props) {
        super(props);

        this.state = {
            image: "",
            name: "", 
            series: "", 
            code: "",
            technical_data: {},
            past: "",

            // valid: true,
            isAuthed: false
        }
    }

    // loginClick = (event) => {
    //     event.preventDefault()
    //     console.log("Login clicked!");
    //     this.setState({
    //         username: event.target.username,
    //         password: event.target.password
    //     })
    //     // console.log(this.state);
    //     // console.log(this.props.users);
    //     this.authUser();
    //     event.target.reset();
    // }

    // authUser = () => {
        // let loggedUser = {username: this.state.username, password: this.state.password};
    //     let id = this.props.match.params;
    //     // console.log("userName"+loggedUser.username)
    //     // let users = this.props.users;
    //     let code = this.props.code;
    //     // console.log("user0Name"+users[0].username)
    //     for(let prdx of code) {
    //         if( id === prdx ) {
    //             this.setState({isAuthed: true},
    //             sessionStorage.setItem('currentUser', JSON.stringify(id))
    //             );
    //             // alert("Login successful!")
    //             return
    //         }
    //     }
    //     // alert("Please enter vaid credentials!");
    //     return
    // }

    componentDidMount() {
        this.props.actions.loadProducts();
        let id = this.props.match.params;
        let code = this.props.code;
        for(let prdx of code) {
            if( id === prdx ) {
                this.setState({image: this.props.image,
                name: this.props.name, 
                series: this.props.series, 
                code: this.props.code,
                technical_data: this.props.technical_data,
                past: this.props.past,
                isAuthed: true},
                sessionStorage.setItem('currentUser', JSON.stringify(id))
                );
                return
            }
        }
        return
    }

    render() {
        // console.log("products: "+this.props.products)
        // const renderProducts = this.props.products.map((product) => <ProductListView product={product} key={product.code} />)
        return (

            <div className={styles.allProducts}>

                <div>< Header /></div>

                <div className={styles.wrapper}>
                    <div className={styles.topLine}>
                        <span className={styles.mechanical}>Mechanical&nbsp;&nbsp;></span><span>&nbsp;&nbsp;HVAC Fans</span>
                    </div>


                    <div>

                        <img src={this.props.product.image} alt = "" className={styles.fan} />
                        <span > {this.props.product.name} &nbsp;&nbsp;> {this.props.product.series} &nbsp;&nbsp;> {this.props.product.code}     {this.props.product.technical_data.size}</span>
                    </div>


                </div> 

            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.products.products
        }
  }

const mapDispatchToProps = (dispatch) => {
    return {actions: bindActionCreators(actions, dispatch)}
}

export default connect(mapStateToProps, mapDispatchToProps)(ProductsPage);
