import React, { Component } from 'react';
// import { connect } from 'react-redux';
// import globalStyles from './bootstrap.min.module.css';
// import cx from 'classnames';
// import { Fragment } from 'react';
import { Redirect } from 'react-router';
import './FilterBar.module.css';
// import styles from './SearchForm.module.css';
// import LoginBackground from './LoginBackground';

class SearchForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            search: '',
            clicked: false,
            redirect: false
        }
    }

    handleOnChange = event => {
        const fanString = "HVAC fans".toLowerCase();
        this.setState({
          [event.target.name]: event.target.value
        });
        if(event.target.value === fanString.slice(0, event.target.value.length) && event.target.value.length > 0) {
            this.setState({clicked: true}, () => {console.log(this.state.valid)})
        } else {
            this.setState({clicked: false}, () => {console.log(this.state.valid)})
        }
    }

    handleSearchClick = () => {
        console.log("Search clicked")
        const clicked = this.state.clicked;
        this.setState({ clicked: !clicked }, () => { console.log(this.state) })
    }

    handleFansClick = () => {
        this.setState({redirect: true}, () => console.log(this.state.redirect))
    }

    renderRedirect = () => {
        if(this.state.redirect) {
            return <Redirect to='/products' />
        }
    }

    render() {
        return (
                <div>
                    <div class="rsg--sidebar-4" data-testid="sidebar">
                        <header class="rsg--logo-5">
                            <h1 class="rsg--logo-42">react-slider</h1>
                        </header>
                        <div>
                            <div class="rsg--root-43">
                                <nav>
                                    <div class="rsg--search-44">
                                        <input class="rsg--input-45" placeholder="Filter by name" aria-label="Filter by name" value=""/>
                                    </div>
                                    <ul class="rsg--list-46">
                                        <li class="rsg--item-47 rsg--isChild-48">
                                            <a class="rsg--link-41" href="/react-slider/#reactslider">ReactSlider</a>
                                        </li>
                                    </ul>
                                </nav>
                            </div>
                        </div>
                    </div>
                </div>
        )
    }
}



export default SearchForm;
