import React, { Component } from 'react';
import styles from './FilterBar.css'
// import { Redirect } from 'react-router';
import './FilterBar.css';
import ReactSlider from 'react-slider';
import styled from 'styled-components';
// import Slider from 'nw-react-slider';


const StyledSlider = styled(ReactSlider)`
    width: 80%;
    height: 3px;
    margin-left: 10%; 
`;

const StyledThumb = styled.div`
    height: 10px;
    line-height: 50px;
    width: 10px;
    margin:-3px
    text-align: center;
    background-color: #000;
    color: #000;
    border-radius: 50%;
    cursor: grab;
`;

const Thumb = (props, state) => <StyledThumb {...props} value = {state.valueNow} >{state.valueNow}</StyledThumb>;

const StyledTrack = styled.div`
    top: 0;
    bottom: 0;
    background: ${props => props.index === 1 ? '#000' : '#ddd'};

`;

const Track = (props, state) => <StyledTrack {...props} index={state.index} />;


class SearchForm extends Component {

    // componentDidMount() {
    //     console.log("filterValue:   "+ this.props)
    //     // this.props.actions.loadRegisteredUsers(); 

    // }

    // handleOnChange = event => {
    //     const fanString = "HVAC fans".toLowerCase();
    //     this.setState({
    //       [event.target.name]: event.target.value
    //     });
    //     if(event.target.value === fanString.slice(0, event.target.value.length) && event.target.value.length > 0) {
    //         this.setState({clicked: true}, () => {console.log(this.state.valid)})
    //     } else {
    //         this.setState({clicked: false}, () => {console.log(this.state.valid)})
    //     }
    // }

    // handleSearchClick = () => {
    //     console.log("Search clicked")
    //     const clicked = this.state.clicked;
    //     this.setState({ clicked: !clicked }, () => { console.log(this.state) })
    // }

    // handleFansClick = () => {
    //     this.setState({redirect: true}, () => console.log(this.state.redirect))
    // }

    // renderRedirect = () => {
    //     if(this.state.redirect) {
    //         return <Redirect to='/products' />
    //     }
    // }

    // handleChange(){
    //     console.log('Change');
    //   }

    sendFilter(e,Thumb){
        console.log("filtersave  "+ Thumb);
    }

    render() {
        console.log("filterValue:   "+ this.props.index)
        return (
            <div className="rsg--sidebar-4" data-testid="sidebar">
                {/* <button onClick = {((e)=> this.sendFilter(e,{Thumb}))}>save</button>
                <div className="attribute">
                    <p> Aitflow </p>
                        <StyledSlider
                            defaultValue={[30, 55]}
                            renderTrack={Track}
                            renderThumb={Thumb}
                            onChange = {((e)=> this.sendFilter(e,{Thumb}))}
                        />

                </div>
                <div className="attribute">
                <p> Max power </p>
                    <StyledSlider
                        defaultValue={[50, 75]}
                        renderTrack={Track}
                        renderThumb={Thumb}
                    />
                </div>
                <div className="attribute">
                <p> Sound at max speed </p>
                    <StyledSlider
                        defaultValue={[0.2,0.4]}
                        renderTrack={Track}
                        renderThumb={Thumb}
                        min = {0}
                        max = {1}
                        step = {0.1}
                    >
                    </StyledSlider>
                </div> */}

                <div className={styles.inner}>
                <div className={styles.lefttable}>
                    <label className={styles.searchLabel}>Search: </label>
                    <button className={styles.button1} onClick={this.save}>Save</button>
                    <button className={styles.button1} onClick={this.clear}>Clear</button><br/>
                    <button className={styles.button2} >Product</button>  
                    <button className={styles.button2} >Project</button>  
                    <p className={styles.title}>  Product Type</p>
                    <p className={styles.smallFont}>Model year: <input onChange={this.handleModelYearMin} value={this.state.ModelYearMin} style={{width: "23%", height:"1.2em"}} type="text"/> - 
                    <input onChange={this.handleModelYearMax} value={this.state.ModelYearMax} style={{width: "23%", height:"1.2em"}} type="text"/></p>
                    <p className={styles.title}>Technical Specification</p>

                    {this.state.filter.map((link, index)=>(
                        <div>
                            <p className={styles.smallFont}>{link.title}</p>
                            <div className={styles.selector}>
                                <input className={styles.minvalue} type="text" value={link.defaultValue[0]}/>
                                <div className={styles.slider}>
                                <StyledSlider
                                    defaultValue={link.defaultValue}
                                    renderTrack={Track}
                                    renderThumb={Thumb}
                                    min={link.valueRange[0]}
                                    max={link.valueRange[1]}
                                    onChange={(value) => this.handleChange(value, index)}
                                    value={link.defaultValue}
                                    /></div>
                                <input className={styles.maxvalue} type="text" value={link.defaultValue[1]}/>

                            </div>
                        </div>
                    ))}
                    <p className={styles.title}>Brand</p>
                    <p className={styles.title}>Past Selections</p>

                    {this.state.filter2.map(link=>(
                    <div>
                        <p className={styles.smallFont}>{link.title}</p>
                            <div className={styles.selector}>
                                <input className={styles.minvalue} type="text" value={link.defaultValue[0]}/>
                                    <div className={styles.slider}>
                                    <StyledSlider
                                        defaultValue={link.defaultValue}
                                        renderTrack={Track}
                                        renderThumb={Thumb}
                                        min={link.valueRange[0]}
                                        max={link.valueRange[1]}
                                        onChange={this.handleChange}
                                        value={link.defaulValue}
                                    /></div>
                                <input className={styles.maxvalue} type="text" value={link.defaultValue[1]}/>
                            </div>
                        </div>
                    ))}
                    <p className={styles.title}>Configurations</p>
                </div>
                <div  className="container">
                    <span style={{color: "rgb(35, 67, 148)", fontSize:"1em", fontWeight:"bold"}}>{this.props.match.params.selection} ></span>
                    <span>{this.props.match.params.searchVal}</span>

                    <Container className="container">
                        <Row>
                            {this.state.filteredData.map(link=>{
                            return(
                            <Col xs={3} className={styles.column}>
                                <div  key={link.manufacturer} id='cardItem' >
                                    <span style={{color:"blue", fontSize:"11px", float:"right"}}>verified {link.modelYear}</span>
                                    <Link to={`/productSummary/${this.props.match.params.selection}/${this.props.match.params.searchVal}/${link.pid}`}>
                                    <img className={styles.fan} src={require(`./${link.manufacturer}.jpg`)} width="50%" height="50%"/>

                                    </Link>
                                    <div className={styles.firstPart}>
                                        <span>{link.manufacturer}</span><br/>
                                        <span>{link.series}</span><br/>
                                        <span>{link.model}</span><br/>

                                    </div>
                                    <div className={styles.secondPart}> 
                                        <span>{link.airflow} CFM</span><br/>
                                        <span>{link.powerMax} W at max speed</span><br/>
                                        <span>{link.soundMaxSpeed} dBA ar max speed</span><br/>
                                        <span>{link.fanSweep} fan sweep diameter</span><br/>
                                    </div>
                                    <div className={styles.thirdPart}>
                                        <span>Past specifications:</span><br/>
                                        <span>{link.firm} firm / {link.glob} global</span>
                                    </div>
                                    <div className={styles.fourthPart}>
                                        <input type="checkbox"
                                            checked={this.state.checkedBoxes.find((ch)=>ch.manufacturer === link.manufacturer)}
                                            onChange={(e) => this.handleCheckbox(e,link)}
                                            /> 

                                        <span>Compare</span>
                                    </div>
                                </div>    
                            </Col>)})} 
                        </Row>
                    </Container>
                    <Link to={{pathname:'/compare', state: this.state.checkedBoxes}}>
                    <button style={{float:"right"}}>Compare</button></Link>
                </div>
            </div>
            </div> 
        );
    }
}



export default SearchForm;
